# swap on a DEX
swapAddress = "address sending"                               # account swapping the tokens

privateKey = "ACCOUNT SENDING PRIVATE KEY"                    # private key of the account sending crypto

# specify token to buy
tokenToBuy = web3.toChecksumAddress('TOKEN ADDRESS')

# specifty token to spend (WBNB)
spend = web3.toChecksumAddress('0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c')

# get nonce for the sender address
nonce = web3.eth.getTransactionCount(swapAddress)

# buy the token when the target price is reached
def buyToken():
    start = time.time() # start of the tx

    # call solidity function
    pancakeV2Tx = routerContract.functions.swapExactETHForTokens(       # swapExactETHForTokens swaps bnb for a token
    0,
    [spend , tokenToBuy],
    swapAddress,
    (int(time.time()) + 10000)
    ).buildTransaction({
        'from': swapAddress,
        'value': web3.toWei(0.1, 'ether'),                              # amount of BNB that we want to swap 
        'gas': 2000000,
        'gasPrice': web3.toWei(10 , 'gwei'), 
        'nonce': nonce
        })
    signtx = web3.eth.account.sign_transaction(pancakeV2Tx, privateKey)    # use the private key to sign the transaction

    # send transaction and sign transaction
    txHash = web3.eth.send_raw_transaction(signtx.rawTransaction)

    # get transaction confirmation and print hash
    print('Transaction Complete, Tx Hash:' , web3.toHex(txHash))

