def checkPrice(tokenAddress):
    routerContract = web3.eth.contract(address=pancakeRouter, abi=pancakeABI)                  # connect to the DEX
    oneToken = web3.toWei(1, 'Ether')                                                          # unit of price we want to receive (1:1 in this case)
    price = routerContract.functions.getAmountsOut(oneToken, [tokenAddress, BUSD]).call()      # call the getAmountsOut function from the DEX smart contract
    normalNumber = web3.fromWei(price[1], 'Ether')
    #value = (f'{normalNumber:.2f}')                                                           # we could use python to cut to 2 decimals 
    return normalNumber

